import styled from "styled-components";

export const Input = styled.input`
    width: 320px;
    height: 60px;
    border-radius: 8px;
    background-color: white;
    font-size: 22px;
    padding-left: 10px;

    &::placeholder {
        font-size: 22px;
        color: "#7E7E7E"
    }

`
